INSERT INTO characters (fullname, house_name, quote) VALUES ('Jon Snow', 'House Stark of Winterfell', 'If I fall, don''t bring me back.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Jon Snow', 'House Stark of Winterfell', 'There is only one war that matters. The Great War. And it is here.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Jon Snow', 'House Stark of Winterfell', 'Love is the death of duty.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Jon Snow', 'House Stark of Winterfell', 'Everything before the word \"but\" is horseshit.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Jon Snow', 'House Stark of Winterfell', 'The war is not over. And I promise you, friend, the true enemy won''t wait out the storm. He brings the storm.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Jon Snow', 'House Stark of Winterfell', 'I hate the king more than any of them.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Jon Snow', 'House Stark of Winterfell', 'No need to seize the last word, Lord Baelish. I''ll assume it was something clever.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Jon Snow', 'House Stark of Winterfell', 'By what right does the wolf judge the lion?');
