INSERT INTO characters (fullname, house_name, quote) VALUES ('Jaime Lannister', 'House Lannister of Casterly Rock', 'The things I do for love.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Jaime Lannister', 'House Lannister of Casterly Rock', 'Fighting bravely for a losing cause is admirable. Fighting for a winning cause is far more rewarding.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Jaime Lannister', 'House Lannister of Casterly Rock', 'Three victories don''t make you a conqueror.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Jaime Lannister', 'House Lannister of Casterly Rock', 'By what right does the wolf judge the lion?');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Tyrion Lannister', 'House Lannister of Casterly Rock', 'Never forget what you are. The rest of the world will not. Wear it like armour, and it can never be used to hurt you.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Tyrion Lannister', 'House Lannister of Casterly Rock', 'The day will come when you think you are safe and happy, and your joy will turn to ashes in your mouth.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Tyrion Lannister', 'House Lannister of Casterly Rock', 'That''s what I do: I drink and I know things.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Daenerys Targaryen', 'House Targaryen of King''s Landing', 'I am the dragon''s daughter, and I swear to you that those who would harm you will die screaming.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Daenerys Targaryen', 'House Targaryen of King''s Landing', 'He was no dragon. Fire cannot kill a dragon.');
INSERT INTO characters (fullname, house_name, quote) VALUES ('Daenerys Targaryen', 'House Targaryen of King''s Landing', 'All men must die, but we are not men.');